import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './registration.css';
import '../loginform/Login.css';
export default class RegistrationForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username: '',
			email: '',
			password: '',
			confirmPassword: ''
		};
	}
	handleSubmit = (event) => {
		event.preventDefault();
	};
	recordValue = (value, typeOfValue) => {
		this.setState({
			[typeOfValue]: value
		});
	};
	render() {
		return (
			<div className="form-container m-350">
				<h1 className="app-name">Register</h1>
				<hr class="line" />
				<Form onClick={this.handleSubmit}>
					<Form.Group controlId="data">
						<Form.Control
							placeholder="Enter username..."
							className="login-input thin-border"
							onChange={(se) => this.recordValue(se.target.value, 'username')}
						/>
						<Form.Control
							placeholder="Enter email..."
							type="email"
							className="login-input thin-border"
							onChange={(se) => this.recordValue(se.target.value, 'email')}
						/>
						<Form.Control
							placeholder="Enter password..."
							type="password"
							className="login-input thin-border"
							onChange={(se) => this.recordValue(se.target.value, 'password')}
						/>
						<Form.Control
							placeholder="Confirm password..."
							type="password"
							className="login-input thin-border"
							onChange={(se) => this.recordValue(se.target.value, 'confirmPassword')}
						/>
					</Form.Group>
					<Button className="button" variant="primary" type="submit">
						Register
					</Button>
				</Form>
			</div>
		);
	}
}
