import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './Login.css';
import { Link } from 'react-router-dom';
import { Alert } from 'react-bootstrap';

export default class Login extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: ''
		};
	}
	validateForm = () => {
		return this.state.email.length > 0 && this.state.password.length > 0;
	};
	handleSubmit = (event) => {
		event.preventDefault();
	};
	render() {
		return (
			<React.Fragment>
				<Alert
					style={{
						position: 'absolute',
						top: 0,
						left: 0,
						width: '100%'
					}}
					variant={'success'}
					show={this.props.location.state.displaySuccessAlert}
				>
					You've registered successfully! Login and explore...
				</Alert>
				<div className="Login Login-Box m-350">
					<h1 className="app-name">PhotoShare</h1>
					{/* <Alert variant={'success'}>You've registered successfully! Login and explore...</Alert> */}
					<Form onClick={this.handleSubmit}>
						<Form.Group controlId="formBasicEmail">
							<br />
							<Form.Control
								type="email"
								placeholder="Enter email..."
								className="login-input thin-border"
							/>
							<br />
							<Form.Control
								type="password"
								placeholder="Enter password..."
								className="login-input thin-border"
							/>
						</Form.Group>
						<Button className="button" variant="primary" type="submit">
							Log In
						</Button>
					</Form>
				</div>
				<div className="Login-Box Bottom-Box">
					Don't have an account? <Link to="/register">Register</Link>
				</div>
			</React.Fragment>
		);
	}
}
