import React from 'react';
import ReactDOM from 'react-dom';
import { Spinner } from 'react-bootstrap';
import { CSSTransition } from 'react-transition-group';
import './loading-screen.css';
export default class LoadingScreen extends React.Component {
	constructor(props) {
		super(props);
		this.el = document.getElementById('root');
		this.state = {};
	}
	createAndAddElement = () => {
		const containerEl = document.createElement('div');
		containerEl.setAttribute('id', 'load-screen');
		containerEl.className = 'load-screen-class';
		this.el.appendChild(containerEl);
		return containerEl;
	};
	componentWillUnmount() {}
	render() {
		// this.el.classList += ' overlay';
		return ReactDOM.createPortal(
			<div className="overlay">
				<div className={`loading-screen-container ${this.props.display ? '' : 'hidden'}`}>
					<div className="vertical-flex-container m-10">
						<Spinner animation="border" role="status" />
						<p>Please Wait...</p>
					</div>
				</div>
			</div>,
			document.getElementById('root')
		);
	}
}
