import { Children } from 'react';
import { Route } from 'react-router';
import { useAuth } from '../auth-context/auth';

export default function ProtectedRoute({ component, ...rest }) {
    const authenticated = useAuth();
    let location = useLocation();
    return (
        <Route
            {...rest}
            render={
                authenticated ? (
                    component
                ) : (
                    <Redirect
                        to={{
                            pathname: '/',
                            state: {
                                from: location,
                            },
                        }}
                    />
                )
            }
        />
    );
}
