import React from 'react';
import ReactDOM from 'react-dom';
import { Spinner } from 'react-bootstrap';
import './loading-screen.css';
export default class LoadingScreen extends React.Component {
	constructor(props) {
		super(props);
		this.el = document.getElementById('root');
		this.state = {};
	}
	createAndAddElement = () => {
		const containerEl = document.createElement('div');
		containerEl.setAttribute('id', 'load-screen');
		containerEl.className = 'load-screen-class';
		this.el.appendChild(containerEl);
		return containerEl;
	};
	componentWillUnmount() {
		const elem = document.getElementById('overlay-screen');
		elem.remove();
	}
	appendOverlayIfNeeded = () => {
		const overlayEl = document.getElementById('overlay-screen');
		if (overlayEl) return;
		overlayEl.setAttribute('id', 'overlay-screen');
		overlayEl.className = 'overlay';
		this.el.appendChild(overlayEl);
	};
	render() {
		let rootClassName = 'loading-screen-container ' + this.props.display ? '' : 'hidden';
		return (
			<React.Fragment>
				<div className={rootClassName}>
					<div className="vertical-flex-container m-10">
						<Spinner animation="border" role="status" />
						<p>Please Wait...</p>
					</div>
				</div>
			</React.Fragment>
		);
	}
}
